            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2256, 'Almond Joy', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xutuqs1483388296.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2257, 'Aloha Fruit punch', 'Add 1/4 cup water to ginger root. Boil 3 minutes. Strain. Add the liquid to the guava, lemon and pineapple juices. Make a syrup of sugar and remaining water. Cool. Combine with juices and pineapple. Chill throroughly.', 'Collins Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wsyvrt1468876267.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2258, 'Amaretto And Cream', 'Shake well with cracked ice, strain contents into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/dj8n0r1504375018.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2259, 'Amaretto Liqueur', 'Combine sugar and 3/4 cup water in a small saucepan. Bring to a boil, stirring constantly. Reduce heat and simmer until all sugar is dissolved. Remove from heat and cool. In an aging container, combine apricot halves, almond extract, grain alcohol with 1/2 cup water, and brandy. Stir in cooled sugar syrup mixture. Cap and let age for 2 days. Remove apricot halves. (Save apricot halves, can be used for cooking). Add food coloring and glycerine. Stir, recap and continue aging for 1 to 2 months. Re-bottle as desired. Liqueur is ready to serve but will continue to improve with additional aging.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/swqxuv1472719649.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2260, 'Amaretto Mist', 'Pour amaretto in an old-fashioned glass over crushed ice. Add the wedge of lime and serve. (A wedge of lemon may be substituted for lime, if preferred.)', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/utpxxq1483388370.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2261, 'Amaretto Rose', 'Pour amaretto and lime juice over ice in a collins glass. Fill with club soda and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/3jm41q1493069578.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2262, 'Amaretto Shake', 'Combine all ingredients in a blender and blend at high speed until smooth. Serve in chilled glass garnished with bittersweet chocolate shavings.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xk79al1493069655.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2263, 'Amaretto Sour', 'Shake and strain. Garnish with a cherry and an orange slice.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xnzc541493070211.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2264, 'Amaretto Stinger', 'Shake ingredients well with cracked ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vvop4w1493069934.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2265, 'Amaretto Stone Sour', 'Shake and Serve over ice', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xwryyx1472719921.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2266, 'Amaretto Stone Sour #3', 'Shake sour mix, tequila and amaretto with ice. Strain into highball glass. Add a splash of OJ. Garnish with orange slice and a cherry.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wutxqr1472720012.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2267, 'Amaretto Sunrise', 'Mix together the amaretto and orange juice. Pour into glass and then add the grenadine untill you see the sunrise.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/akcpsh1493070267.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2268, 'Amaretto Sunset', 'Shake ingredients in bartender''s mixer quickly, just 5 shakes. Strain out ice, serve in glass immediately with a slice of orange.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/apictz1493069760.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2269, 'Amaretto Sweet & Sour', 'Fill the blender with 3/4 ice. Add sweet & sour mix to the top of the ice. Add about 1\" of pineapple juice, 1/2\" of melon liqeur, and 1/2 to 1/4\" of amaretto. Then blend the mix until it is of margaritta consistency or thinner.', 'Margarita/Coupette glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vswwus1472668546.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2270, 'Amaretto Tea', 'Pour hot tea into a pousse-cafe glass, using a spoon in glass to prevent cracking. Add amaretto, but do not stir. Top with chilled whipped cream and serve.', 'Pousse cafe glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/b7qzo21493070167.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2271, 'Americano', 'Pour the Campari and vermouth over ice into glass, add a splash of soda water and garnish with half orange slice.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/trwruu1478253126.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2272, 'Angel Face', 'Shake all ingredients with ice and strain contents into a cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vqpptp1478253178.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2273, 'Arthur Tompkins', 'In a shaker half-filled with ice cubes, combine the gin, Grand Marnier, and lemon juice. Shake well. Strain into a sour glass and garnish with the lemon twist.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/7onfhz1493067921.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2274, 'Angelica Liqueur', 'Combine all herbs, nuts and spices with vodka in a 1 quart or larger aging container. Cap tightly and shake daily for 2 weeks. Strain through a fine muslin cloth or coffee filter, discarding solids. Clean out aging container. Place liquid back in container. Place sugar and water in saucepan and stir to combine over medium heat. When sugar is completely dissolved, set aside and let cool. When cool combine with food coloring and add to liqueur liquid. Cap and allow to age and mellow in a cool, dark place for one month.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yuurps1472667672.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2275, 'Apello', 'Stirr. Grnish with maraschino cherry.', 'Collins Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uptxtv1468876415.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2276, 'Apple Berry Smoothie', 'Throw everything into a blender and liquify.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xwqvur1468876473.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2277, 'Apple Cider Punch #1', 'If you use the whole all spice and cloves, tie them in cheesecloth. Heat the mixture. Stir occasionally. If you want an alcoholic drink, rum would be nice.', 'Collins Glass', NULL, 'Optional alcohol', 'http://www.thecocktaildb.com/images/media/drink/xrqxuv1454513218.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2278, 'Apple Grande', 'Chill both ingredients!! Mix in a tumbler and enjoy!', 'Punch Bowl', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wqrptx1472668622.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2279, 'Apple Karate', 'Place all ingredients in the blender jar - cover and whiz on medium speed until well blended. Pour in one tall, 2 medium or 3 small glasses and drink up.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/syusvw1468876634.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2280, 'Apple Pie with A Crust', 'Just mix the two liquids and sprinkle in the cinnamon. Serve either cold or heated.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qspqxt1472720078.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2281, 'Apple Slammer', 'pour into a shot glass and present to consumer, they are expected to cover the top of the shotglass with thier palm, raise the glass, slam it on the bar and the swallow quickly.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/09yd5f1493069852.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2282, 'Applecar', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sbffau1504389764.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2283, 'Applejack', 'Add all ingredients into mixing glass, chill and strain into cocktail glass', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sutyqp1479209062.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2284, 'Apricot Lady', 'In a shaker half-filled with ice cubes, combine the rum, apricot brandy, triple sec, lemon juice, and egg white. Shake well. Strain into an old-fashioned glass almost filled with ice cubes. Garnish with the orange slice.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/146f251504389814.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2285, 'Apricot punch', 'Pour all ingrediants into a large punch bowl. Add ice and 4 oranges that are peeled and divided.', 'Punch bowl', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tuxxtp1472668667.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2286, 'Archbishop', 'In an old-fashioned glass almost filled with ice cubes, combine all of the ingredients. Stir well.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xpqwrt1441207307.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2287, 'Arctic Fish', 'Fill glass with ice and fish, add vodka, grape soda and orange juice. DO NOT STIR!!!!! Serve well chilled.', 'Beer pilsner', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ttsvwy1472668781.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2288, 'Arctic Mouthwash', 'Blend all ingredients in a blender on high until ice is finely crushed. It should be of a slushy consistency. Pour immediately and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wqstwv1478963735.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2289, 'Arise My Love', 'Put creme de menthe into a champagne flute. Fill with chilled champagne and serve.', 'Champagne flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wyrrwv1441207432.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2290, 'Arizona Antifreeze', 'Pour all ingredients into shot glass and slam !!!!', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/dbtylp1493067262.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2291, 'Arizona Stingers', 'Place ice cubes in the hurricane glass . Add the 2 HEAPING shots of Absolute Vodka (Note: You can add as many shots of Absolute as you want!) Fill the rest of glass with the Arizona Icetea with lemon. Stir to mix using a spoon. Drink up and enjoy!!!!!!!', 'Hurricane glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/y7w0721493068255.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2292, 'Arizona Twister', 'Just mix in the shots of rum, vodka, and tequila. Add splashes of the three juices, heavy on the pineapple. Top off with grenadine. Crushed ice should already be in glass. Top off the glass with a pineapple wedge.', 'Hurricane glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ido1j01493068134.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2293, 'Army special', 'Pour Vodka, Gin and lime cordial into glass, and top up with crushed ice. Wait for ice to melt slightly and sip without a straw.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/55muhh1493068062.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2294, 'Artillery', 'Stir all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/g1vnbe1493067747.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2295, 'Artillery Punch', 'Combine all the ingredients in a large punch bowl with a block of ice. If found too dry, sugar syrup may be added. Decorate with twists of lemon peel.', 'Punch bowl', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/9a4vqb1493067692.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2296, 'Atlantic Sun', 'Shake all the ingredients, top the drink with soda. Garnish with a slice of orange.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/doyxqb1493067556.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2297, 'Atomic Lokade', 'In a shaker, place lemonade, vodka, blue Curacao, and triple sec together. Shake with ice and strain into glass. Add sugar to taste', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/n3zfrh1493067412.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2298, 'Auburn Headbanger', 'Mix in spread glass over ice. Strain and pour in shot glass. Sit down before shotting. ENJOY!!', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vw7iv91493067320.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2299, 'AutodafÃ©', 'Mix and fill up with soda water. Drunk by finns on a sunny day any time of the year and day.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/7dkf0i1487602928.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2300, 'Avalanche', 'Mix in highball glass over ice, shake well.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uppqty1472720165.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2703, 'Veteran', 'Pour the rum and cherry brandy into an old-fashioned glass almost filled with ice cubes. Stir well.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/iwml9t1492976255.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2301, 'Avalon', 'Fill a tall glass with ice. Layer the Finlandia Vodka, lemon and apple juices, Pisang Ambon, and top up with lemonade. Stir slightly and garnish with a spiralled cucumber skin and a red cherry. The cucumber provides zest and looks attractive. This drink, created by Timo Haimi, took first prize in the 1991 Finlandia Vodka Long Drink Competition.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/3k9qic1493068931.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2302, 'Aviation', 'Add all ingredients into cocktail shaker filled with ice. Shake well and strain into cocktail glass. Garnish with a cherry.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ruutxt1478253328.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2303, 'Aztec Punch', 'Mix all ingredients in a pitcher. Mix thoroughly and pour into whatever is available, the bigger the better! This drink packs a big punch, so don''t over do it.', 'Punch bowl', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uqwuyp1454514591.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2304, 'B-52', 'Layer ingredients into a shot glass. Serve with a stirrer.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/5a3vg61504372070.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2305, 'B-53', 'Layer the Kahlua, Sambucca and Grand Marnier into a shot glas in that order. Better than B-52', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rwqxrv1461866023.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2306, 'Baby Eskimo', 'Leave ice-cream out for about 10 minutes. Add ingredients in order, stir with chopstick (butter knife or spoon works too). Consume immediately and often. Nice and light, great for following a heavy drink.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wywrtw1472720227.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2307, 'Baby Guinness', 'Pour Kahlua, almost filling shot glass. Then, carefully pour Baileys, using wall of shot glass. This will give the \"Guinness\" its \"head\".', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rvyvxs1473482359.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2308, 'Bacardi Cocktail', 'Shake together with ice. Strain into glass and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/n433t21504348259.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2309, 'Bailey''s Dream Shake', 'Blend ingredients for 30 seconds. Definitely refreshing for a hot summer''s day !', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qxrvqw1472718959.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2310, 'Balmoral', 'In a mixing glass half-filled with ice cubes, combine all of the ingredients. Stir well. Strain into a cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vysuyq1441206297.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2311, 'Banana Cantaloupe Smoothie', 'Juice cantaloupe, pour juice into blender, add banana, and liquify.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uqxqsy1468876703.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2312, 'Banana Daiquiri', 'Pour all ingredients into shaker with ice cubes. Shake well. Strain in chilled cocktail glass.', 'Champagne flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/k1xatq1504389300.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2313, 'Banana Milk Shake', 'Blend very well, preferably in a household mixer. Serve in a wine glass, garnish with whipped cream and a piece of banana.', 'White wine glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rtwwsx1472720307.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2314, 'Banana Strawberry Shake', 'Blend all together in a blender until smooth.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vqquwx1472720634.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2315, 'Banana Strawberry Shake Daiquiri-type', 'Blend all together in a blender until smooth.', 'Cocktail Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uvypss1472720581.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2316, 'Barracuda', 'Shake pour ingredients with ice. Strain into glass, top with Sparkling wine.', 'Margarita glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/jwmr1x1504372337.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2317, 'Belgian Blue', 'Just pour all ingredients in the glass and stir...', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rttsxw1441245419.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2318, 'Bellini', 'Pour peach purÃ©e into chilled flute, add sparkling wine. Stir gently.', 'Champagne Flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/eaag491504367543.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2319, 'Bellini Martini', 'Add ice cubes to shaker.Add vodka.Add peach schnapps.Add peach nectar.Shake.Strain into glass.Add lemon twist peel.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/3h9wv51504389379.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2320, 'Bermuda Highball', 'Pour brandy, gin, and dry vermouth into a highball glass over ice cubes. Fill with carbonated water and stir. Add the twist of lemon and serve. (Ginger ale may be substituted for carbonated water, if preferred.)', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qrvtww1441206528.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2321, 'Berry Deadly', 'Add all ingredients to large bowl. Stir gently. Serve chilled.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xqutpr1461867477.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2322, 'Between The Sheets', 'Pour all ingredients into shaker with ice cubes, shake, strain into chilled cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/of1rj41504348346.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2323, 'Bible Belt', 'Mix all ingredients, and pour over ice.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/6bec6v1503563675.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2324, 'Big Red', 'Pour ingredients into 1 ounce shot glass', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yqwuwu1441248116.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2325, 'Black & Tan', 'Fill pint glass half full with Bass. Next pour Guiness over a spoon slowly until glass is full. If done correctly the Guiness will stay on top and the Bass on bottom hence the name Black & Tan.', 'Pint glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rwpswp1454511017.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2326, 'Black Forest Shake', 'In a blender put ice cubes, chocolate syrup, cherry brandy, vodka, and milk. Blend very well.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xxtxsu1472720505.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2327, 'Black Russian', 'Pour the ingredients into an old fashioned glass filled with ice cubes. Stir gently.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/2k5gbb1504367689.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2328, 'Black and Brown', 'CAREFULLY to avoid explosive head formation: Pour Beer glass half full of favorite rootbeer and top off with Guinness.', 'Beer pilsner', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wwuvxv1472668899.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2329, 'Blackthorn', 'Stir sloe gin and vermouth with ice and strain into a cocktail glass. Add the twist of lemon peel and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xvswvy1441209430.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2330, 'Bleeding Surgeon', 'Pour Shot of Rum over slice of orange. Fill the remaining space in glass half way full of surge or similar drink. Finish off glass with cranberry juice. Be carefull, warm surge may foam over the glass.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/usuvvr1472719118.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2331, 'Blind Russian', 'Fill glass with ice. Add all liquers. Add milk. shake.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wxuqvr1472720408.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2332, 'Bloody Maria', 'Shake all ingredients (except lemon slice) with cracked ice and strain into an old-fashioned glass over ice cubes. Add the slice of lemon and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yz0j6z1504389461.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2333, 'Bloody Mary', 'Stirring gently, pour all ingredients into highball glass. Garnish.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uyquuu1439906954.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2334, 'Blue Lagoon', 'Pour vodka and curacao over ice in a highball glass. Fill with lemonade, top with the cherry, and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vak0e51504389504.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2335, 'Blue Margarita', 'Rub rim of cocktail glass with lime juice. Dip rim in coarse salt. Shake tequila, blue curacao, and lime juice with ice, strain into the salt-rimmed glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qtvvyq1439905913.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2336, 'Blue Mountain', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into an old-fashioned glass alomst filled with ice cubes.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vsxssp1441209853.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2337, 'Bluebird', 'In a mixing glass half-filled with crushed ice, combine the gin, triple sec, Curacao, and bitters. Stir well. Strain into a cocktail glass and garnish with the lemon twist and the cherry.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vxypsq1441209971.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2338, 'Bob Marley', 'Layer in a 2 oz shot glass or pony glass', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rrqrst1477140664.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2339, 'Bobby Burns Cocktail', 'Stir all ingredients (except lemon peel) with ice and strain into a cocktail glass. Add the twist of lemon peel and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/km6se51484411608.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2340, 'Boomerang', 'In a mixing glass half-filled with ice cubes, combine the gin, vermouth, bitters, and maraschino liqueur. Stir well. Strain into a cocktail glass and garnish with the cherry.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/3m6yz81504389551.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2341, 'Boozy Snickers Milkshake', 'Place the snickers bars in a plastic bag and roll over them with a rolling pin until crushed. Add crushed snickers pieces, ice cream, milk, caramel sauce, chocolate sauce, and chocolate liquor to a blender. Blend until shake is thick and frothy. Pour into glasses and top with chocolate liquor and whip cream.', 'Mason jar', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/861tzm1504784164.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2342, 'Bora Bora', 'Prepare in a blender or shaker, serve in a highball glass on the rocks. Garnish with 1 slice of pineapple and one cherry.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xwuqvw1473201811.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2343, 'Boston Sidecar', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qzs5d11504365962.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2344, 'Boston Sour', 'Shake juice of lemon, powdered sugar, blended whiskey, and egg white with cracked ice and strain into a whiskey sour glass. Add the slice of lemon, top with the cherry, and serve.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/kxlgbi1504366100.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2345, 'Bourbon Sling', 'In a shaker half-filled with ice cubes, combine the sugar, water, lemon juice, and bourbon. Shake well. Strain well. Strain into a highball glass. Garnish with the lemon twist.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/3s36ql1504366260.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2346, 'Bourbon Sour', 'In a shaker half-filled with ice cubes, combine the bourbon, lemon juice, and sugar. Shake well. Strain into a whiskey sour glass, garnish with the orange slice and cherry.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/dms3io1504366318.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2347, 'Boxcar', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a sour glass.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/pwgtpa1504366376.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2348, 'Brain Fart', 'Mix all ingredients together. Slowly and gently. Works best if ice is added to punch bowl and soda''s are very cold.', 'Punch bowl', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rz5aun1504389701.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2349, 'Brainteaser', 'layered erin first, then sambuca and then avocart(should sit in middle of other two. To drink: use a straw to suck up avocart then shot the rest and then suck fumes up through straw.', 'Shot Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ruywtq1461866066.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2350, 'Bramble', 'Fill glass with crushed ice. Build gin, lemon juice and simple syrup over. Stir, and then pour blackberry liqueur over in a circular fashion to create marbling effect. Garnish with two blackberries and lemon slice.', 'Old-Fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/lvzl3r1504372526.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2351, 'Brandon and Will''s Coke Float', 'Scoop two large scoops of vanilla ice-cream into frosted beer mug. Next, add 2 ounces Maker''s Mark. Then, pour in coke. Gently stir and enjoy.', 'Beer mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xspxyr1472719185.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2352, 'Brandy Alexander', 'Shake all ingredients (except nutmeg) with ice and strain contents into a cocktail glass. Sprinkle nutmeg on top and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tvqxvr1483387746.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2353, 'Brandy Cobbler', 'In an old-fashioned glass, dissolve the sugar in the club soda. Add crushed ice until the glass is almost full. Add the brandy. Stir well. Garnish with the cherry and the orange and lemon slices.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vtvutr1439465836.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2354, 'Brandy Flip', 'In a shaker half-filled with ice cubes, combine the brandy, egg, sugar, and cream. Shake well. Strain into a sour glass and garnish with the nutmeg.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/6ty09d1504366461.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2355, 'Brandy Sour', 'Shake brandy, juice of lemon, and powdered sugar with ice and strain into a whiskey sour glass. Decorate with the lemon slice, top with the cherry, and serve.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tu0mts1504366540.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2356, 'Brave Bull Shooter', 'Pour Tabasco into bottom of shot glass and fill with tequila.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yrtypx1473344625.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2357, 'Kool First Aid', 'Add Kool Aid to a double shot glass, and top with rum. Slam and shoot.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/hfp6sv1503564824.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2358, 'Bruce''s Puce', 'In a regular-sized shot glass, layer, with a spoon or cherry, the grenadine , the Kahlua , then the Bailey''s Irish cream in equal portions. It goes down really smooth ,and you don''t even need a chaser. It tastes just like chocolate milk.(Really!)', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/svsvqv1473344558.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2359, 'Bruised Heart', 'Pour all ingredients in a mixing tin over ice, stir, and strain into shot glass', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/7if5kq1503564209.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2360, 'Bubble Gum', 'Layer in order into a shot glass.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/spuurv1468878783.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2361, 'Buccaneer', 'Pour the corona into an 18oz beer glass pour the bacardi limon into the beer stir very gently', 'Beer pilsner', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/upvtyt1441249023.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2362, 'Bumble Bee #1', 'This is a layered shot. First pour the Bailey''s into the shot glass. Then take an upside down spoon and touch it to the inside wall of the glass. Carefully add the Kahlua. Repeat this process for the Sambuca. If done properly, the alcohol will stay separated and resemble a bumble bee. Enjoy!!!', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uwqpvv1461866378.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2363, 'Butter Baby', 'Blend together in a blender. Serve in a chilled Beer mug with Fresh Blueberries and caramel for topping.', 'Beer mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/1xhjk91504783763.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2364, 'Cafe Savoy', 'Fill mug almost to top with coffee.Add milk, triple sec and brandy. Stir.', 'Coffee mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vqwptt1441247711.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2365, 'Caipirinha', 'Place lime and sugar into old fashioned glass and muddle (mash the two ingredients together using a muddler or a wooden spoon). Fill the glass with ice and add the CachaÃ§a.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xyrppt1469090521.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2366, 'Caipirissima', 'Same as Caipirinha but instead of cachaca you add WHITE RUM. It''s great!!!!!!!!', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yd47111503565515.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2367, 'California Lemonade', 'Shake all ingredients (except carbonated water) with ice and strain into a collins glass over shaved ice. Fill with carbonated water and stir. Decorate with slices of orange and lemon. Add the cherry and serve with a straw.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qvvxwy1472406424.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2368, 'California Root Beer', 'Put Kahlua and Galliano in highball glass fill with soda', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rsxuyr1472719526.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2369, 'Campari Beer', 'Use a 15 oz glass. Add Campari first. Fill with beer.', 'Beer mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xsqrup1441249130.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2370, 'Caribbean Boilermaker', 'Pour the Corona into an 18oz beer glass pour the rum into the beer.', 'Beer pilsner', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/svsxsv1454511666.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2371, 'Caribbean Orange Liqueur', 'Pare very thinly the bright-colored rind from the oranges (no white). Blot the peel on paper towels to remove any excess oil. Put peel in a 4 cup screw-top jar. Add 2 cups vodka. Close jar. Store in a cool, dark place for 2 days or until the vodka has absorbed the flavor. Remove peel and add remaining vodka. Close jar and add remaining cup of vodka. Close the jar and store in a cool dark place at least 1 month to age.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qwxuwy1472667570.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2372, 'Casino', 'Pour all ingredients into shaker with ice cubes. Shake well. Strain into chilled cocktail glass. Garnish with a lemon twist and a maraschino cherry. Serve without a straw.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/1mvjxg1504348579.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2373, 'Casino Royale', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a sour glass.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/3qpv121504366699.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2374, 'Castillian Hot Chocolate', 'Shift the cocoa and sugar together into a medium-sized saucepan. Dissolve the cornstarch in the water, and stir into the cocoa and sugar until it is a smooth paste. Begin heating the mixture, stirring it with a whisk, and gradually pour in the milk. Continue stirring with the whisk as you bring the liquid to a simmer. Allow the chocolate to simmer for about 10 minutes, stirring often, until it is thick, glossy and completely smooth. Serve steaming hot in coffee mug. Serves six.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/3nbu4a1487603196.jpg', 'Cocoa');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2375, 'Champagne Cocktail', 'Add dash of Angostura bitter onto sugar cube and drop it into champagne flute. Add cognac followed by gently pouring chilled champagne. Garnish with orange slice and maraschino cherry.', 'Champagne flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ehh5df1504366811.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2376, 'Cherry Electric Lemonade', 'Now stir vigorously and then pour over a large cup of ice. Now drink it with a straw and stir occasionally.', 'Pint glass', NULL, NULL, 'http://www.thecocktaildb.com/images/media/drink/tquyyt1451299548.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2377, 'Cherry Rum', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/twsuvr1441554424.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2378, 'Chicago Fizz', 'Shake all ingredients (except carbonated water) with ice and strain into a highball glass over two ice cubes. Fill with carbonated water, stir, and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qwvwqr1441207763.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2379, 'Chocolate Beverage', 'Boil milk in the top of a deep double boiler five minutes. Remove from fire and add chocolate, mixed with the cinnamon, a little at a time, beating with molinillo or egg beater after each addition. When the chocolate is thoroughly blended, heat to the boiling point. Place over bottom of double boiler and add eggs, whipping constantly, until they are thoroughly blended and the mixture is frothing. Serve in coffee mug. Serves eight.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/jbqrhv1487603186.jpg', 'Cocoa');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2380, 'Chocolate Black Russian', 'Combine all ingredients in an electric blender and blend at a low speed for a short length of time. Pour into a chilled champagne flute and serve.', 'Champagne flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yyvywx1472720879.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2381, 'Chocolate Drink', 'Melt the bar in a small amount of boiling water. Add milk. Cook over low heat, whipping gently (with a whisk, i would assume) until heated well. Don''t let it boil! Serve in coffee mug.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/q7w4xu1487603180.jpg', 'Cocoa');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2382, 'Chocolate Milk', 'Put the milk in the bottom, pour the Liquer on top and add the dash of amaretto. Do not mix. SLAM IT!', 'Shot Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/j6q35t1504889399.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2383, 'Chocolate Monkey', 'blend liqeuors with ice-cream, milk and syrup. pour into parfait glass, top with whipped cream and garnish with banana and cherry.', 'Parfait glass', NULL, 'Optional alcohol', 'http://www.thecocktaildb.com/images/media/drink/tyvpxt1468875212.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2384, 'Citrus Coke', 'Pour half of coke in a glass. Then add Bacardi and top it off with the remaining coke. Stir and drink up!', 'Highball Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uyrvut1479473214.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2385, 'City Slicker', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/dazdlg1504366949.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2386, 'Classic Old-Fashioned', 'In an old-fashioned glass, muddle the bitters and water into the sugar cube, using the back of a teaspoon. Almost fill the glass with ice cubes and add the bourbon. Garnish with the orange slice and the cherry. Serve with a swizzle stick.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/anhof61504366173.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2387, 'Clove Cocktail', 'Stir all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qxvtst1461867579.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2388, 'Clover Club', 'Dry shake ingredients to emulsify, add ice, shake and served straight up.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/t0aja61504348715.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2389, 'Coffee Liqueur', 'Combine coffee, sugar and water. Simmer 1 hour and let cool. Add vanilla and vodka. Age in sealed jar 2 to 3 weeks.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ryvtsu1441253851.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2390, 'Coffee-Vodka', 'Boil water and sugar until dissolved. Turn off heat. Slowly add dry instant coffee and continue stirring. Add a chopped vanilla bean to the vodka, then combine the cooled sugar syrup and coffee solution with the vodka. Cover tightly and shake vigorously each day for 3 weeks. Strain and filter. Its also best to let the sugar mixture cool completely so the vodka won''t evaporate when its added. If you like a smoother feel to the liqueur you can add about 1 teaspoon of glycerine to the finished product.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qvrrvu1472667494.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2391, 'Coke and Drops', 'Take a glass, pour the Coke in the glass, then you take 7 drops of lemon juice. Granish with a lemon slice on the rim of the glass.', 'Cocktail glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yrtxxp1472719367.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2392, 'Cosmopolitan', 'Add all ingredients into cocktail shaker filled with ice. Shake well and double strain into large cocktail glass. Garnish with lime wheel.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/kpsajh1504368362.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2393, 'Cosmopolitan Martini', 'Pour all ingredients in mixing glass half filled with ice, shake and strain into chilled Martini glass.', 'Cocktail Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/upxxpq1439907580.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2394, 'Cranberry Cordial', 'Place the chopped cranberries in a 2 liter jar that has a tight-fitting lid. Add the sugar and rum. Adjust the lid securely and place the jar in a cool, dark place. Invert the jar and shake it every day for six weeks. Strain the cordial into bottles and seal with corks.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qtspsx1472667392.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2395, 'Cranberry Punch', 'Combine first four ingredients. Stir until sugar is dissolved, chill. Then add ginger ale just before serving. Add ice ring to keep punch cold.', 'Punch Bowl', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/mzgaqu1504389248.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2396, 'Cream Soda', 'Pour 1oz of Spiced Rum into a highball glass with ice. Fill with Ginger Ale.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yqstxr1479209367.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2397, 'Creme de Menthe', 'Bring sugar and water to a boil and simmer for 10 minutes. Cool. Add the remaining ingredients and stir. Cover and let ripen for 1 month.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yxswtp1441253918.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2398, 'Cuba Libra', 'Fill tall glass with ice cubes. Add rum. Rub cut edge of lime on rim of glass then squeeze juice into glass. Fill with Coca-Cola. Garnish with lime slice. Enjoy!', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ck6d0p1504388696.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2399, 'Cuba Libre', 'Build all ingredients in a Collins glass filled with ice. Garnish with lime wedge.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uuxsrr1473201663.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2400, 'Daiquiri', 'Pour all ingredients into shaker with ice cubes. Shake well. Strain in chilled cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/usuuur1439906797.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2401, 'Damned if you do', 'Pour into shot glass. Put in mouth. Repeat as deemed necessary.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ql7bmx1503565106.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2402, 'Danbooka', 'pour it in and mix it.', 'Coffee Mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vurrxr1441246074.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2403, 'Dark Caipirinha', 'Muddle the sugar into the lime wedges in an old-fashioned glass.Fill the glass with ice cubes.Pour the cachaca into the glass.Stir well.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uwstrx1472406058.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2404, 'Dark and Stormy', 'In a highball glass filled with ice add 6cl dark rum and top with ginger beer. Garnish with lime wedge.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/t1tn0s1504374905.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2405, 'Darkwood Sling', 'There are many good cherry liqueurs you can use, but I prefere Heering. Add one share of the liqueur. Then you add one share of Soda. For a sour sling use Tonic (most people prefer the drink without Tonic). Afterwards you fill the glass with Orange Juice and ice cubes.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sxxsyq1472719303.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2406, 'Derby', 'Pour all ingredients into a mixing glass with ice. Stir. Strain into a cocktail glass. Garnish with a sprig of fresh mint in the drink.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/i502ra1504349156.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2407, 'Diesel', 'Pour the lager first then add the blackcurrant cordial. Top up with the cider. The colour sholud be very dark approaching the colour of Guiness.', 'Pint glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sxrrqq1454512852.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2408, 'Dirty Martini', 'Pour the vodka, dry vermouth and olive brine into a cocktail shaker with a handful of ice and shake well.Rub the rim of a martini glass with the wedge of lemon.Strain the contents of the cocktail shaker into the glass and add the olive.A dirty Martini contains a splash of olive brine or olive juice and is typically garnished with an olive.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vcyvpq1485083300.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2409, 'Dirty Nipple', 'This is a layered shot - the Bailey''s must be on top', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vtyqrt1461866508.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2410, 'Downshift', 'Start with the Sprite. Next comes the tequila. After that, add the Minute Maid Fruit Punch, then float the 151. Rocks optional.', 'Hurricane glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/y36z8c1503563911.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2411, 'Dragonfly', 'In a highball glass almost filled with ice cubes, combine the gin and ginger ale. Stir well. Garnish with the lime wedge.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/lnvbip1504389100.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2412, 'Drinking Chocolate', 'Heat the cream and milk with the cinnamon and vanilla bean very slowly for 15-20 minutes. (If you don''t have any beans add 1-2 tsp of vanilla after heating). Remove the bean and cinnamon. Add the chocolate. Mix until fully melted. Serve topped with some very dense fresh whipped cream. Serves 1-2 depending upon how much of a glutton you are. For a richer chocolate, use 4 oz of milk, 4 oz of cream, 4 oz of chocolate. Serve in coffee mug.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/u6jrdf1487603173.jpg', 'Cocoa');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2413, 'Dry Rob Roy', 'In a mixing glass half-filled with ice cubes, combine the Scotch and vermouth. Stir well. Strain into a cocktail glass. Garnish with the lemon twist.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/typuyq1439456976.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2414, 'Dubonnet Cocktail', 'Stir all ingredients (except lemon peel) with ice and strain into a cocktail glass. Add the twist of lemon peel and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xxsuwr1439457147.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2415, 'Duchamp''s Punch', 'Shake all ingredients.Double strain in a chilled double old fashioned glass with abig ice cube.Garnish with a couple of lavender sprigs', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/g51naw1485084685.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2416, 'Egg Cream', 'Mix syrup and milk in a fountain glass. Add soda water, serve with a straw.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/mvis731484430445.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2417, 'Harvey Wallbanger', 'Stir the vodka and orange juice with ice in the glass, then float the Galliano on top. Garnish and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vg4bva1504369725.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2418, 'Egg Nog #4', 'In a small mixer bowl beat egg yolks till blended. Gradually add 1/4 cup sugar, beating at high speed till thick and lemon colored. Stir in milk, stir in rum, bourbon, vanilla, and salt. Chill thoroughly. Whip cream. Wash beaters well. In a large mixer bowl beat egg whites till soft peaks form. Gradually add remaining 1/4 cup sugar, beating to stiff peaks. Fold yolk mixture and whipped cream into egg whites. Serve immediately. Sprinkle nutmeg over each serving. Serve in a punch bowl or another big bowl. NOTE: For a nonalcoholic eggnog, prepare Eggnog as above, except omit the bourbon and rum and increase the milk to 3 cups.', 'Punch bowl', NULL, 'Optional alcohol', 'http://www.thecocktaildb.com/images/media/drink/wpspsy1468875747.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2419, 'Egg Nog - Healthy', 'Whip egg substitute and sugar together, combine with the two kinds of milk, vanilla, and rum. Mix well. Chill over night. Sprinkle with nutmeg. Makes 6 servings.', 'Collins Glass', NULL, 'Optional alcohol', 'http://www.thecocktaildb.com/images/media/drink/qxuppv1468875308.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2420, 'Egg-Nog - Classic Cooked', 'In large saucepan, beat together eggs, sugar and salt, if desired. Stir in 2 cups of the milk. Cook over low heat, stirring constantly, until mixture is thick enough to coat a metal spoon and reaches 160 degrees F. Remove from heat. Stir in remaining 2 cups milk and vanilla. Cover and regfigerate until thoroughly chilled, several hours or overnight. Just before serving, pour into bowl or pitcher. Garnish or add stir-ins, if desired. Choose 1 or several of: Chocolate curls, cinnamon sticks, extracts of flavorings, flavored brandy or liqueur, fruit juice or nectar, ground nutmeg, maraschino cherries, orange slices, peppermint sticks or candy canes, plain brandy, run or whiskey, sherbet or ice-cream, whipping cream, whipped. Serve immediately.', 'Pitcher', NULL, 'Optional alcohol', 'http://www.thecocktaildb.com/images/media/drink/quxsvt1468875505.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2421, 'English Highball', 'Pour brandy, gin, and sweet vermouth into a highball glass over ice cubes. Fill with carbonated water. Add the twist of lemon peel, stir, and serve. (Ginger ale may be substituted for carbonated water, if preferred.)', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/dhvr7d1504519752.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2422, 'English Rose Cocktail', 'Rub rim of cocktail glass with lemon juice and dip rim of glass in powdered sugar. Shake all ingredients (except cherry) with ice and strain into sugar-rimmed glass. Top with the cherry and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yxwrpp1441208697.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2423, 'Espresso Martini', 'Pour ingredients into shaker filled with ice, shake vigorously, and strain into chilled martini glass', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/n0sx531504372951.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2424, 'Fahrenheit 5000', 'Cover bottom of shot glass with Tabasco Sauce and then fill with half Firewater and half Absolut Peppar.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tysssx1473344692.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2425, 'Flaming Dr. Pepper', 'Add Amaretto, Bacardi, and vodka. Mix in the Dr. Pepper and beer', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/d30z931503565384.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2426, 'Flaming Lamborghini', 'Pour the Sambuca and Kahlua into the Cocktail Glass and give the drinker a straw. Pour the Baileys and Blue Curacao into two sepsrate shot glasses either side of the cocktail glass. Set light the concotion in the cocktail glass and start to drink through the straw (this drink should be drunk in one) , as the bottom of the glass is reached put out the fire by pouring the Baileys and Blue Curacao into the cocktail glass and keep drinking till it''s all gone!!', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yywpss1461866587.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2427, 'Flander''s Flake-Out', 'Bang ''em both in.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sqvqrx1461866705.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2428, 'Flying Dutchman', 'In an old-fashioned glass almost filled with ice cubes, combine the gin and triple sec. Stir well.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/akik8t1504519958.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2429, 'Flying Scotchman', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/glgrix1504520040.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2430, 'Foxy Lady', 'Shake all ingredients with ice, strain into a chilled cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/r9cz3q1504519844.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2704, 'Victor', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/voapgc1492976416.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2431, 'FrappÃ©', 'Mix together. Blend at highest blender speed for about 1 minute. Pour into a glass and drink with a straw. Notes: This works best if everything is cold (if you make fresh coffee, mix it with the milk and let it sit in the fridge for 1/2 hour. If it is not frothy, add more milk, or even just some more milk powder. The froth gradually turns to liquid at the bottom of the glass, so you will find that you can sit and drink this for about 1/2 hour, with more iced coffee continually appearing at the bottom. Very refreshing.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vqwryq1441245927.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2432, 'Freddy Kruger', 'make it an ample size shot!!', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tuppuq1461866798.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2433, 'French \"75\"', 'In a shaker half-filled with ice cubes, combine the gin, sugar, and lemon juice. Shake well. Pour into a collins glass. Top with the Champagne. Stir well and garnish with the orange slice and the cherry.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qpvxsx1439906903.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2434, 'Kiwi Lemon', 'Mix in highball glass. Stirr. Garnish with slice of kiwi.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tpupvr1478251697.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2435, 'French 75', 'Combine gin, sugar, and lemon juice in a cocktail shaker filled with ice. Shake vigorously and strain into a chilled champagne glass. Top up with Champagne. Stir gently.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/4qnyty1504368615.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2436, 'French Connection', 'Pour all ingredients directly into old fashioned glass filled with ice cubes. Stir gently.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/zaqa381504368758.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2437, 'French Martini', 'Pour all ingredients into shaker with ice cubes. Shake well and strain into a chilled cocktail glass. Squeeze oil from lemon peel onto the drink.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/clth721504373134.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2438, 'Frisco Sour', 'Shake all ingredients (except slices of lemon and lime) with ice and strain into a whiskey sour glass. Decorate with the slices of lemon and lime and serve.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/y39l941504884150.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2439, 'Frozen Daiquiri', 'Combine all ingredients (except for the cherry) in an electric blender and blend at a low speed for five seconds, then blend at a high speed until firm. Pour contents into a champagne flute, top with the cherry, and serve.', 'Champagne flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/7oyrj91504884412.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2440, 'Frozen Mint Daiquiri', 'Combine all ingredients with 1 cup of crushed ice in an electric blender. Blend at a low speed for a short length of time. Pour into an old-fashioned glass and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/jrhn1q1504884469.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2441, 'Frozen Pineapple Daiquiri', 'Combine all ingredients with 1 cup of crushed ice in an electric blender. Blend at a low speed for a short length of time. Pour into a champagne flute and serve.', 'Champagne flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/j2mqsb1504736097.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2442, 'Fruit Cooler', 'Toss strawberries with sugar, and let sit overnight in refrigerator. Cut lemon, reserve two slices. Juice the rest. Mix together the lemon juice, strawberries, apple juice, and soda water. Add slices of lemon (decor, really). In glasses, put ice cubes, and a slice of apple. Pour drink in, and serve.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/i3tfn31484430499.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2443, 'Fruit Flip-Flop', 'Place all ingredients in the blender jar - cover and whiz on medium speed until well blended. Pour in one tall, 2 medium or 3 small glasses and drink up.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/nfdx6p1484430633.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2444, 'Fruit Shake', 'Blend til smooth.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/q0fg2m1484430704.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2445, 'Fuzzy Asshole', 'fill coffe mug half full of coffee. Fill the other half full of Peach Schnapps. Stir and drink while hot.', 'Coffee mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wrvpuu1472667898.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2446, 'GG', 'Pour the Galliano liqueur over ice. Fill the remainder of the glass with ginger ale and thats all there is to it. You now have a your very own GG.', 'Collins Glass', NULL, 'Optional alcohol', 'http://www.thecocktaildb.com/images/media/drink/vyxwut1468875960.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2447, 'Gagliardo', 'Shake well and serve in a cocktail glass. This is a home cocktail of American/Internet Bar del Pozzo, Pavia, Italy.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/lyloe91487602877.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2448, 'Gentleman''s Club', 'In an old-fashioned glass almost filled with ice cubes, combine all of the ingredients. Stir well.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ezvy1c1504883335.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2449, 'Gideon''s Green Dinosaur', 'Add all ingredients in collins glass with ice and stir.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/p5r0tr1503564636.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2450, 'Gin And Tonic', 'Pour the gin and the tonic water into a highball glass almost filled with ice cubes. Stir well. Garnish with the lime wedge.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wtyxvx1472405831.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2451, 'Gin Cooler', 'Stir powdered sugar and 2 oz. carbonated water in a collins glass. Fill glass with ice and add gin. Fill with carbonated water and stir. Add the lemon peel and the orange spiral so that the end of the orange spiral dangles over rim of glass.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wsyrvv1478820109.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2452, 'Gin Daisy', 'In a shaker half-filled with ice cubes, combine the wine, lemon juice, sugar, and grenadine. Shake well. Pour into an old-fashioned glass and garnish with the cherry and the orange slice.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qwxysv1478819943.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2453, 'Gin Fizz', 'Shake all ingredients with ice cubes, except soda water. Pour into glass. Top with soda water.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xhl8q31504351772.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2454, 'Gin Rickey', 'Half-fill a tall glass with ice. Mix the gin and Grenadine together and pour over the ice. Add the lime or lemon juice and top off with soda water. Decorate the glass with lime and/or lemon slices.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/s00d6f1504883945.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2455, 'Gin Sling', 'Dissolve powdered sugar in mixture of water and juice of lemon. Add gin. Pour into an old-fashioned glass over ice cubes and stir. Add the twist of orange peel and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/h8wmm41504883440.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2456, 'Gin Smash', 'Muddle sugar with carbonated water and mint sprigs in an old-fashioned glass. Add gin and 1 ice cube. Stir, add the orange slice and the cherry, and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/hp41fi1504883656.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2457, 'Gin Sour', 'In a shaker half-filled with ice cubes, combine the gin, lemon juice, and sugar. Shake well. Strain into a sour glass and garnish with the orange slice and the cherry.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/mt7l7m1504883523.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2458, 'Gin Squirt', 'Stir gin, grenadine, and powdered sugar with ice and strain into a highball glass over ice cubes. Fill with carbonated water and stir. Decorate with the pineapple chunks and the strawberries and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xrbhz61504883702.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2459, 'Gin Swizzle', 'In a shaker half-filled with ice cubes, combine the lime juice, sugar, gin, and bitters. Shake well. Almost fill a colling glass with ice cubes. Stir until the glass is frosted. Strain the mixture in the shaker into the glass and add the club soda.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sybce31504884026.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2460, 'Gin Toddy', 'Mix powdered sugar and water in an old-fashioned glass. Add gin and one ice cube. Stir, add the twist of lemon peel, and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/l9gi5h1504820724.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2461, 'Girl From Ipanema', 'Add the cachaca, lemon juice and syrup to your boston glass. Add ice and shake until ice cold. Pour into a chilled flute and top-up with Champagne', 'Wine Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xypspq1469090633.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2462, 'Gluehwein', 'Boil sugar and spices in water, leave in the water for 30 minutes. Strain the spiced water and mix with the wine. Heat slowly until short of boiling temperature. (To remove alcohol, let it boil for a while.) You may add lemon or orange juice to taste. Serve in irish coffee cup.', 'Irish coffee cup', NULL, 'Optional alcohol', 'http://www.thecocktaildb.com/images/media/drink/vuxwvt1468875418.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2463, 'Godchild', 'Shake all ingredients well with cracked ice, strain into a champagne flute, and serve.', 'Champagne flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/m5nhtr1504820829.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2464, 'Godfather', 'Pour all ingredients directly into old fashioned glass filled with ice cubes. Stir gently.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/r74uz51504368928.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2465, 'Godmother', 'Pour vodka and amaretto into an old-fashioned glass over ice and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/4889up1504368984.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2466, 'Golden dream', 'Shake with cracked ice. Strain into glass and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qrot6j1504369425.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2467, 'Grand Blue', 'Serve in an old fashioned glass.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vsrsqu1472761749.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2468, 'Grape lemon pineapple Smoothie', 'Throw everything into a blender and liquify.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/54z5h71487603583.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2469, 'Grass Skirt', 'In a shaker half-filled with ice cubes, combine the gin, triple sec, pineapple juice, and grenadine. Shake well. Pour into an old-fashioned glass and garnish with the pineapple slice.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qyvprp1473891585.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2470, 'Grasshopper', 'Pour ingredients into a cocktail shaker with ice. Shake briskly and then strain into a chilled cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/aqm9el1504369613.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2471, 'Green Goblin', 'Cider First, Lager then Curacao', 'Pint glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qxprxr1454511520.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2472, 'Grim Reaper', 'Mix Kahlua and 151 in glass. Quickly add ice and pour grenadine over ice to give ice red tint.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/kztu161504883192.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2473, 'Grizzly Bear', 'Served over ice. Sounds nasty, but tastes great.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/k6v97f1487602550.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2474, 'H.D.', 'Mix the whisky and Baileys Cream in a beer-glass (at least 50 cl). Fill the rest of the glass with coffee.', 'Beer mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/upusyu1472667977.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2475, 'Happy Skipper', 'Pour Captain Morgan''s Spiced Rum over ice, fill glass to top with Ginger Ale. Garnish with lime. Tastes like a cream soda. Named for the Gilligan''s Island reference (\"The Captain\" *in* \"Ginger\" is a Happy Skipper!)', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/42w2g41487602448.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2476, 'Havana Cocktail', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/59splc1504882899.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2477, 'Hawaiian Cocktail', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ujoh9x1504882987.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2478, 'Hemingway Special', 'Pour all ingredients into a shaker with ice. Shake.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/jfcvps1504369888.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2479, 'Herbal flame', 'Pour Hot Damn 100 in bottom of a jar or regular glass. Fill the rest of the glass with sweet tea. Stir with spoon, straw, or better yet a cinnamon stick and leave it in.', 'Mason jar', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rrstxv1441246184.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2480, 'Highland Fling Cocktail', 'Stir all ingredients (except olive) with ice and strain into a cocktail glass. Add the olive and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/0bkwca1492975553.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2481, 'Holloween Punch', 'Take a bunch of grape juice and a bunch of fizzy stuff (club soda, ginger ale, lemonlime, whatever). Mix them in a punch bowl. Take orange sherbet and lime sherbet. Scoop out scoops and float them in the punch, let them melt a little so that a nasty film spreads all over the top of the punch but there are still \"bubbles\" in it in the form of sherbet scoops. Looks horrible, tastes just fine.', 'Punch bowl', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/lfeoe41504888925.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2482, 'Homemade Kahlua', 'Dissolve sugar in 2 cups of boiling water and add corn syrup. Dissolve the instant coffee in the remaining water. Pour syrup and coffee in a gallon jug. Let it cool. Add vodka and vanilla when cold. For the best result, let the mixture \"mature\" for 4-5 weeks.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uwtsst1441254025.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2483, 'Horse''s Neck', 'Pour brandy and ginger ale directly into highball glass with ice cubes. Stir gently. Garnish with lemon zest. If desired, add dashes of Angostura Bitter.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/006k4e1504370092.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2484, 'Hot Chocolate to Die for', 'Melt the chocolate, butter and vanilla in a double boiler. When just smooth stir in the cream.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/0lrmjp1487603166.jpg', 'Cocoa');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2485, 'Hot Creamy Bush', 'Combine all ingredients in glass', 'Irish coffee cup', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/spvrtp1472668037.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2486, 'Ice Pick #1', 'Put Vodka in glass fill with iced tea. Stir in lemon to taste.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ypsrqp1469091726.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2487, 'Iced Coffee', 'Mix together until coffee and sugar is dissolved. Add milk. Shake well. Using a blender or milk shake maker produces a very foamy drink. Serve in coffee mug.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ytprxy1454513855.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2488, 'Iced Coffee Fillip', 'Mix together in a coffee mug and chill before serving.', 'Coffee mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sxtxrp1454514223.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2489, 'Imperial Cocktail', 'Shake with ice and strain into cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/bcsj2e1487603625.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2490, 'Imperial Fizz', 'Shake all ingredients (except carbonated water) with ice and strain into a highball glass over two ice cubes. Fill with carbonated water, stir, and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/zj1usl1504884548.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2491, 'Ipamena', 'Cut half a lime into pieces, place in a shaker, add the sugar and crush. Measure the passion fruit juice, add it to the shaker and fill up with ice cubes. Close the shaker and shake vigorously. Pour the liquid into a glass, top up with ginger ale, stir with a teaspoon and then garnish the rim of the glass with a slice of lime', 'Wine Glass', NULL, 'Non Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yswuwp1469090992.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2492, 'Irish Coffee', 'Heat the coffee, whiskey and sugar; do not boil. Pour into glass and top with cream; serve hot.', 'Irish coffee cup', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sywsqw1439906999.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2493, 'Irish Cream', 'Mix scotch and milk. Add half-and-half. Add rest.', 'Irish coffee cup', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/90etyl1504884699.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2494, 'Irish Curdling Cow', 'Pour Irish Cream, Vodka, and Bourbon in a glass. Add some ice and mix in the orange juice.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yrhutv1503563730.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2495, 'Irish Russian', 'Add the ingredients in the order listed in the recipe. Care must be taken when adding the Guinness to prevent an excess of foam. Do Not add ice.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/swqurw1454512730.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2496, 'Kiwi Papaya Smoothie', 'Throw everything into a blender and liquify.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/jogv4w1487603571.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2497, 'Irish Spring', 'Pour all ingredients (except orange slice and cherry) into a collins glass over ice cubes. Garnish with the slice of orange, add the cherry on top, and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sot8v41504884783.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2498, 'Jack Rose Cocktail', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uuqqrv1439907068.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2499, 'Jack''s Vanilla Coke', 'After pouring in your ingredients, and adding 3-5 ice cubes, according to taste. Stir the drink with a stirrer to get the Vanilla off the bottom.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/kjnt7z1504793319.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2500, 'Jackhammer', 'Serve over ice- Warning,Deadly!', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/9von5j1504388896.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2501, 'Jam Donut', 'Coat the rim of a shot glass with sugar using sugar syrup to stick. Add the Chambord raspberry liqueur to the shot glass, and carefully layer the Baileys Irish Cream on top. Serve.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uuytrp1474039804.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2502, 'Jamaica Kiss', 'Fill a tumbler with ice cubes. Add a shot of Tia Maria and a shot of Jamaican light rum. Fill the tumbler with milk. Blend until smooth and serve immediately.', 'Hurricane glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/urpvvv1441249549.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2503, 'Jamaican Coffee', 'Stir the rum, coffee and water together. Top with the whipped cream. Sprinkle with a pinch of well ground coffee and drink with a straw.', 'Champagne flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xqptps1441247257.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2504, 'Japanese Fizz', 'Shake all ingredients (except carbonated water) with ice and strain into a highball glass over two ice cubes. Fill with carbonated water, stir, and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/37vzv11504884831.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2505, 'Jello shots', 'Boil 3 cups of water then add jello. Mix jello and water until jello is completely disolved. Add the two cups of vodka and mix together. Pour mixture into plastic shot glasses and chill until firm. Then, eat away...', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/l0smzo1504884904.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2506, 'Jelly Bean', 'mix equal parts in pony glass-tastes just like a jelly bean!', 'Cordial glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/bglc6y1504388797.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2507, 'Jewel Of The Nile', 'In a mixing glass half-filled with ice cubes, combine all of the ingredients. Stir well. Strain into a cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/hx4nrb1504884947.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2508, 'Jitterbug', 'Wet glass, dip rim in sugar. Then add Ice. Then add everything else. It''s that simple!', 'Cocktail Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wwqvrq1441245318.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2509, 'John Collins', 'Pour all ingredients directly into highball glass filled with ice. Stir gently. Garnish. Add a dash of Angostura bitters.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/u5yaxl1504350270.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2510, 'Just a Moonmint', 'Place all ingredients in the blender jar - cover and whiz on medium speed until well blended. Pour in one tall, 2 medium or 3 small glasses and drink up.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/znald61487604035.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2511, 'Kamikaze', 'Shake all ingredients together with ice. Strain into glass, garnish and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xa58bb1504373204.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2512, 'Karsk', 'Put a copper coin in a coffe-cup and fill up with coffee until you no longer see the coin, then add alcohol until you see the coin. Norwegian speciality.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/808mxk1487602471.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2513, 'Kentucky B And B', 'Pour the bourbon and Benedictine into a brandy snifter.', 'Brandy snifter', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sqxsxp1478820236.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2514, 'Kentucky Colonel', 'In a shaker half-filled with ice cubes combine the courbon and Benedictine. Shake and strain into a cocktail glass. Garnish with the lemon twist.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/utqwpu1478820348.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2515, 'Kill the cold Smoothie', 'Juice ginger and lemon and add it to hot water. You may add cardomom.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/7j1z2e1487603414.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2516, 'Kioki Coffee', 'Stir. Add whipped cream to the top.', 'Coffee mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uppqty1441247374.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2517, 'Kir', 'Add the crÃ¨me de cassis to the bottom of the glass, then top up with wine.', 'Wine Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/apneom1504370294.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2518, 'Kir Royale', 'Pour Creme de cassis in glass, gently pour champagne on top', 'Champagne Flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yt9i7n1504370388.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2519, 'Kiss me Quick', 'mix in the glass', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/m7iaxu1504885119.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2520, 'Kool-Aid Shot', 'Pour into a large glass with ice and stir. Add a little cranberry juice to taste.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/fegm621503564966.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2521, 'Kool-Aid Slammer', 'Fill half the shot glass with the kool-aid first. Then put a paper towel over the top of the glass and slowly pour in the vodka. If you do it right, you should be able to see that the two liquids are separated, with the vodka on top. Now slam it! The last thing you''ll taste is the kool-aid.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/kugu2m1504735473.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2522, 'Kurant Tea', 'Pour Absolut Kurant into a comfortably big tea-cup. Add the not too hot(!) apple tea and, if you like, some sugar. Enjoy!', 'Champagne flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xrsrpr1441247464.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2523, 'Lady Love Fizz', 'Shake all ingredients (except carbonated water) with ice and strain into a cocktail glass over two ice cubes. Fill with carbonated water, stir, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/20d63k1504885263.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2524, 'Lassi - A South Indian Drink', 'Blend in a blender for 3 seconds. Lassi is one of the easiest things to make, and there are many ways of making it. Basically, it is buttermilk (yoghurt whisked with water), and you can choose almost any consistency that you like, from the thinnest to the thickest. Serve cold.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/iq6scx1487603980.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2525, 'Lassi - Mango', 'Put it all in a blender and pour over crushed ice. You can also use other fruits like strawberries and bananas.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/1bw6sd1487603816.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2526, 'Lassi - Sweet', 'Put all ingredients into a blender and blend until nice and frothy. Serve chilled.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/9jeifz1487603885.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2527, 'Lassi Khara', 'Blend (frappe) in blender until frothy. Add torn curry leaves and serve cold.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/m1suzm1487603970.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2528, 'Lassi Raita', 'Blend the yoghurt and ice cubes together, until the yoghurt becomes more liquid. Add sugar to taste. The lemon/lime is optional but it gives it a slightly tart taste. Dash of salt. Raita is also good for the summer. Instead of having a traditional salad you can make raita instead.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/s4x0qj1487603933.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2529, 'Lemon Drop', 'Shake and strain into a chilled cocktail glass rimmed with sugar.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/mtpxgk1504373297.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2530, 'Lemon Shot', 'Mix Galliano and Absolut Citron in a shot glass, lay lemon wedge sprinkled with sugar over glass and pour a rum over wedge and glass. light rum with a lighter and let burn for a second. Do shot quickly and suck on lemon. If it is done correctly, this will taste like a shot of sweet lemonade.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/mx31hv1487602979.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2531, 'Lemouroudji', 'Juice the lemons. Peel and grate the ginger. Place the grated ginger and a liberal dash of the cayenne pepper into a piece of cheesecloth, and tie it into a knot. Let soak in the water. After 15 minutes or so, add the sugar, and the lemon juice. Chill, and serve.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/eirmo71487603745.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2532, 'Limeade', 'In a large glass, put the lime juice and sugar, and stir well. Add cold seltzer water to fill. Put the lime peels in the glass. Drink. Repeat until limes or soda run out.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/5jdp5r1487603680.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2533, 'Limona Corona', 'Open the Corona. Fill the empty space in the neck in the bottle with the rum. The bottle should be filled to the top. Plug the bottle with your thumb or the palm of your hand. Turn the bottle upside-down so the rum and beer mix. Turn the bottle rightside-up, unplug, and drink.', 'Beer Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wwqrsw1441248662.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2534, 'Loch Lomond', 'In a mixing glass half-filled with ice cubes, combine the Scotch, Drambuie, and vermouth. Stir well. Strain into a cocktail glass. Garnish with the lemon twist.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rpvtpr1468923881.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2535, 'London Town', 'In a mixing glass half-filled with ice cubes, combine all of the ingredients. Stir well. Strain into a cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rpsrqv1468923507.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2536, 'Lone Tree Cocktail', 'Stir ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tsxpty1468923417.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2537, 'Lone Tree Cooler', 'Stir powdered sugar and 2 oz. carbonated water in a collins glass. Fill glass with ice, add gin and vermouth, and stir. Fill with carbonated water and stir again. Add the twist of lemon peel and the orange spiral so that the end dangles over rim of glass.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wsyqry1479298485.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2538, 'Long Island Iced Tea', 'Mix all contents in a highball glass and sitr gently. Add dash of Coca-Cola for the coloring and garnish with lemon or lime twist.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wx7hsg1504370510.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2539, 'Long Island Tea', 'Combine all ingredients (except cola) and pour over ice in a highball glass. Add the splash of cola for color. Decorate with a slice of lemon and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ywxwqs1439906072.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2540, 'Long vodka', 'Shake a tall glass with ice cubes and Angostura, coating the inside of the glass. Por the vodka onto this, add 1 slice of lime and squeeze juice out of remainder, mix with tonic, stir and voila you have a Long Vodka', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/9179i01503565212.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2541, 'Lord And Lady', 'Pour the rum and Tia Maria into an old-fashioned glass almost filled with ice cubes. Stir well.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/quwrys1468923219.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2542, 'Lunch Box', 'Fill a pint glass almost full with beer. Then fill the rest with orange juice (careful not to fill it to the top). Then take the shot of Amaretto and drop it in.', 'Pint glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qywpvt1454512546.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2543, 'Mai Tai', 'Shake all ingredients with ice. Strain into glass. Garnish and serve with straw.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/twyrrp1439907470.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2544, 'Malibu Twister', 'Add rum & trister then, add cranberry jucie,stir', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/2dwae41504885321.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2545, 'Mango Orange Smoothie', 'Throw everything into a blender and liquify.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vdp2do1487603520.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2546, 'Manhattan', 'Stirred over ice, strained into a chilled glass, garnished, and served up.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ec2jtz1504350429.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2547, 'Margarita', 'Rub the rim of the glass with the lime slice to make the salt stick to it. Take care to moisten only the outer rim and sprinkle the salt on it. The salt should present to the lips of the imbiber and never mix into the cocktail. Shake the other ingredients with ice, then carefully pour into the glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wpxpvu1439905379.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2548, 'Martinez Cocktail', 'Stir all ingredients (except cherry) with ice and strain into a cocktail glass. Top with the cherry and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wwxwvr1439906452.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2549, 'Martini', 'Straight: Pour all ingredients into mixing glass with ice cubes. Stir well. Strain in chilled martini cocktail glass. Squeeze oil from lemon peel onto the drink, or garnish with olive.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/71t8581504353095.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2550, 'Mary Pickford', 'Shake and strain into a chilled large cocktail glass', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/f9erqb1504350557.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2551, 'Masala Chai', 'Bring 2 cups of water to boil. Add all the ingredients and boil again for about 15 seconds. Let stand for a minute. Warm milk in a pot. Filter tea into cups. Add milk and sugar. That''s IT.', 'Coffee Mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uyrpww1441246384.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2552, 'Melya', 'Brew espresso. In a coffee mug, place 1 teaspoon of unsweetened powdered cocoa, then cover a teaspoon with honey and drizzle it into the cup. Stir while the coffee brews, this is the fun part. The cocoa seems to coat the honey without mixing, so you get a dusty, sticky mass that looks as though it will never mix. Then all at once, presto! It looks like dark chocolate sauce. Pour hot espresso over the honey, stirring to dissolve. Serve with cream.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xwtptq1441247579.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2553, 'Miami Vice', 'First: Mix pina colada with 2.5 oz. of rum with ice(set aside). Second: Mix daiquiri with 2.5 oz. of rum with ice. Third: While frozen, add pina colda mix then daiquiri mix in glass (Making sure they do not get mixed together).', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qvuyqw1441208955.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2554, 'Microwave Hot Cocoa', 'Combine sugar, cocoa, salt and hot water in 1-quart micro-proof measuring cup (or coffee mug). Microwave at HIGH (100%) for 1 to 1 1/2 minutes or until boiling. Add milk, sitr and microwave an additonal 1 1/2 to 2 minutes or until hot. Stir in vanilla, blend well.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/8y4x5f1487603151.jpg', 'Cocoa');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2555, 'Midnight Cowboy', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vsxxwy1441208133.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2556, 'Midnight Manx', 'Fill a mixer with ice and add Baileys, Kahlua, Goldshlager, and cream. Shake for 5 seconds and Strain into a double rocks glass filled with ice. Add chilled coffee Stir and enjoy!', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uqqurp1441208231.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2557, 'Midnight Mint', 'If available, rim cocktail (Martini) glass with sugar syrup then dip into chocolate flakes or powder. Add ingredients into shaker with ice. Shake well then strain into cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/svuvrq1441208310.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2558, 'Mimosa', 'Ensure both ingredients are well chilled, then mix into the glass. Serve cold.', 'Champagne flute', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/juhcuu1504370685.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2559, 'Mississippi Planters Punch', 'Shake all ingredients (except carbonated water) with ice and strain into a collins glass over ice cubes. Fill with carbonated water, stir, and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/urpyqs1439907531.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2560, 'Mocha-Berry', 'pour 6 oz. of coffee in a mug or Irish coffee cup. add coca mix and chambord, mix well and top off with whipped cream.', 'Irish coffee cup', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vtwyyx1441246448.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2561, 'Mojito', 'Muddle mint leaves with sugar and lime juice. Add a splash of soda water and fill the glass with cracked ice. Pour the rum and top with soda water. Garnish and serve with straw.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rxtqps1478251029.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2562, 'Mojito #3', 'Put mint with lemon juice in a glas, mash the mint with a spoon, ice, rum & fill up with club soda. Top it with Angostura.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vwxrsw1478251483.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2563, 'Monkey Gland', 'Shake well over ice cubes in a shaker, strain into a chilled cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/94psp81504350690.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2564, 'Monkey Wrench', 'Pour all of the ingredients into an old-fashioned glass almost filled with ice cubes. Stir well.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/8it05n1504353314.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2565, 'Moranguito', 'first you put rhe absinthe, then put tequila, then put the Granadine syrup.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/urpsyq1475667335.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2566, 'Moscow Mule', 'Combine vodka and ginger beer in a highball glass filled with ice. Add lime juice. Stir gently. Garnish.', 'Copper Mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/3pylqc1504370988.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2567, 'Mother''s Milk', 'Shake over ice, strain. Serves two.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/7stuuh1504885399.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2568, 'Mudslinger', 'Add all contents to a large jug or punch bowl. Stir well!', 'Punch bowl', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/hepk6h1504885554.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2569, 'Mulled Wine', 'Simmer 3 cups water with, sugar, cloves, cinnamon sticks, and lemon peel in a stainless steel pot for 10 minutes. Add wine heat to a \"coffee temperature\" (DO NOT BOIL) then add the brandy.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/iuwi6h1504735724.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2570, 'National Aquarium', 'Pour all ingredients into a shaker of ice. Shake well. Serve on the rocks.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/dlw0om1503565021.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2571, 'Negroni', 'Stir into glass over ice, garnish and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tutwwv1439907127.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2572, 'New York Lemonade', 'Serve in a chilled cocktail glass. Lemon and sugar the rim. Stir and Strain.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/b3n0ge1503565473.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2573, 'New York Sour', 'Shake blended whiskey, juice of lemon, and powdered sugar with ice and strain into a whiskey sour glass. Float claret on top. Decorate with the half-slice of lemon and the cherry and serve.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/61wgch1504882795.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2574, 'Nuked Hot Chocolate', 'Mix with a bit of milk (1 oz or so) in coffee mug. Nuke mug for about 30-50 seconds. Stir until the heated cocoa dissolves. Fill mug with milk. Nuke for 1-2 minutes, depending on wattage and preferences as to burnt mouth parts.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xcu6nb1487603142.jpg', 'Cocoa');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2575, 'Nutty Irishman', 'Serve over ice', 'Highball Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xspupx1441248014.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2576, 'Old Fashioned', 'Place sugar cube in old fashioned glass and saturate with bitters, add a dash of plain water. Muddle until dissolved.Fill the glass with ice cubes and add whiskey.Garnish with orange twist, and a cocktail cherry.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vrwquq1478252802.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2577, 'Orange Crush', 'Add all ingredients to tumbler-Pour as shot', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/zvoics1504885926.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2578, 'Orange Oasis', 'Shake brandy, gin, and orange juice with ice and strain into a highball glass over ice cubes. Fill with ginger ale, stir, and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xqqytq1441167923.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2579, 'Orange Push-up', 'Combine liquors in a blender. Add a half scoop of ice and blend. Garnish with an orange and cherry flag. So good it will melt in your mouth!!!', 'Hurricane glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/mgf0y91503565781.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2580, 'Orange Scented Hot Chocolate', 'Combine all ingredients in heavy medium saucepan. Stir over low heat until chocolate melts. Increase heat and bring just to a boil, stirring often. Remove from heat and whisk untily frothy. Return to heat and bring to boil again. Remove from heat, whisk until frothy. Repeat heating and whisking once again. Discard orange peel. (Can be prepared 2 hours ahead. Let stand at room temperature. Before serving, bring just to boil, remove from heat and whisk until frothy.) Pour hot chocolate into coffee mugs. Makes 2 servings.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/hdzwrh1487603131.jpg', 'Cocoa');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2581, 'Orange Whip', 'Pour ingredients over ice and stir.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ttyrxr1454514759.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2582, 'Orangeade', 'Place some ice cubes in a large tumbler or highball glass, add lemon juice, orange juice, sugar syrup, and stir well. Top up with cold soda water, serve with a drinking straw.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ytsxxw1441167732.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2583, 'Oreo Mudslide', 'Blend Vodka, Kahlua, Bailey''s, ice-cream and the Oreo well in a blender. Pour into a large frosted glass. Garnish with whipped cream and a cherry.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tpwwut1468925017.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2584, 'Orgasm', 'Shake all ingredients with ice, strain into a chilled cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vr6kle1504886114.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2585, 'Owen''s Grandmother''s Revenge', 'Add ingredients and mix in blender.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/0wt4uo1503565321.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2586, 'Paradise', 'Shake together over ice. Strain into cocktail glass and serve chilled.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ejozd71504351060.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2587, 'Pina Colada', 'Mix with crushed ice in blender until smooth. Pour into chilled glass, garnish and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/cpf4j51504371346.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2588, 'Pineapple Gingerale Smoothie', 'Throw everything into a blender and liquify.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/eg9i1d1487603469.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2589, 'Pink Gin', 'Pour the bitters into a wine glass. Swirl the glass to coat the inside with the bitters, shake out the excess. Pour the gin into the glass. Do not add ice.', 'White wine glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qyr51e1504888618.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2590, 'Pink Lady', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/5ia6j21504887829.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2591, 'Pink Panty Pulldowns', 'Shake well', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/squsuy1468926657.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2592, 'Pink Penocha', 'mix all ingredients into bowl keep iced stir frequently', 'Punch bowl', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/6vigjx1503564007.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2593, 'Pisco Sour', 'Vigorously shake and strain contents in a cocktail shaker with ice cubes, then pour into glass and garnish with bitters.[1]', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tsssur1439907622.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2594, 'Planter''s Punch', 'Pour all ingredients, except the bitters, into shaker filled with ice. Shake well. Pour into large glass, filled with ice. Add Angostura bitters, \"on top\". Garnish with cocktail cherry and pineapple.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yvos231504351384.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2595, 'Popped cherry', 'Served over ice in a tall glass with a popped cherry (can add more popped cherries if in the mood)!', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sxvrwv1473344825.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2596, 'Poppy Cocktail', 'Shake ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/cslw1w1504389915.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2597, 'Port And Starboard', 'Pour carefully into a pousse-cafe glass, so that creme de menthe floats on grenadine. Serve without mixing.', 'Pousse cafe glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wxvupx1441553911.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2598, 'Port Wine Cocktail', 'Stir ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qruprq1441553976.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2599, 'Port Wine Flip', 'Shake all ingredients (except nutmeg) with ice and strain into a whiskey sour glass. Sprinkle nutmeg on top and serve.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vrprxu1441553844.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2600, 'Porto flip', 'Shake ingredients together in a mixer with ice. Strain into glass, garnish and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/64x5j41504351518.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2601, 'Pysch Vitamin Light', 'Shake with ice.', 'Collins Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xsqsxw1441553580.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2602, 'Zorro', 'add all and pour black coffee and add whipped cream on top.', 'Coffee Mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/kvvd4z1485621283.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2603, 'Quaker''s Cocktail', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yrqppx1478962314.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2604, 'Quarter Deck Cocktail', 'Stir all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qrwvps1478963017.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2605, 'Queen Bee', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rvvpxu1478963194.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2606, 'Queen Charlotte', 'Pour red wine and grenadine into a collins glass over ice cubes. Fill with lemon-lime soda, stir, and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vqruyt1478963249.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2607, 'Queen Elizabeth', 'Stir all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vpqspv1478963339.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2608, 'Quentin', 'In a shaker half-filled with ice cubes, combine the rum, Kahlua, and cream. Shake well. Strain into a cocktail glass and garnish with the nutmeg.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/spxtqp1478963398.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2609, 'Quick F**K', 'In a shot glass add 1/3 Kahlua first. Then 1/3 Miduri, topping it off with a 1/3 bailey''s irish cream', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wvtwpp1478963454.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2610, 'Quick-sand', 'Simply add the orange juice, quite a quick pour in order to mix the sambucca with the orange juice. The juice MUST have fruit pulp!', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vprxqv1478963533.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2611, 'Radioactive Long Island Iced Tea', 'Pour all ingredients over ice in a very tall glass. Sip cautiously.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rdvqmh1503563512.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2612, 'Radler', 'Pour beer into large mug, slowly add the 7-up (or Sprite).', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xz8igv1504888995.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2613, 'Rail Splitter', 'Mix sugar syrup with lemon juice in a tall glass. Fill up with ginger ale.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/stsuqq1441207660.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2614, 'Raspberry Cooler', 'Pour the raspberry vodka and soda into a highball glass almost filled with ice cubes. Stir well.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/suqyyx1441254346.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2615, 'Red Snapper', 'One shot each, shake n shoot', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/7p607y1504735343.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2616, 'Rose', 'Shake together in a cocktail shaker, then strain into chilled glass. Garnish and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/8kxbvq1504371462.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2617, 'Royal Bitch', 'Into a shot glass layer the Crown Royal on top of the Frangelico.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qupuyr1441210090.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2618, 'Royal Fizz', 'Shake all ingredients (except cola) with ice and strain into a chilled collins glass. Fill with cola and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wrh44j1504390609.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2619, 'Royal Flush', 'Pour all the ingredients into tumbler over ice. Strain into glass.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/7rnm8u1504888527.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2620, 'Royal Gin Fizz', 'Shake all ingredients (except carbonated water) with ice and strain into a highball glass over two ice cubes. Fill with carbonated water, stir, and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/pe1x1c1504735672.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2621, 'Ruby Tuesday', 'Pour gin and cranberry into a highball filled with ice cubes. Add grenadine and stir.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qsyqqq1441553437.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2622, 'Rum Cobbler', 'In an old-fashioned glass, dissolve the sugar in the club soda. Add crushed ice until the glass is almost full. Add the rum. Stir well. Garnish with the cherry and the orange and lemon slices.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/5vh9ld1504390683.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2623, 'Rum Cooler', 'Pour the rum and soda into a collins glass almost filled with ice cubes. Stir well and garnish with the lemon wedge.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/2hgwsb1504888674.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2624, 'Rum Milk Punch', 'Shake all ingredients (except nutmeg) with ice and strain into a collins glass. Sprinkle nutmeg on top and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/w64lqm1504888810.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2705, 'Victory Collins', 'Shake all ingredients (except orange slice) with ice and strain into a collins glass over ice cubes. Add the slice of orange and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/lx0lvs1492976619.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2706, 'Vodka And Tonic', 'Pour vodka into a highball glass over ice cubes. Fill with tonic water, stir, and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/lmj2yt1504820500.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2707, 'Vodka Fizz', 'Blend all ingredients, save nutmeg. Pour into large white wine glass and sprinkle nutmeg on top.', 'White wine glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xwxyux1441254243.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2708, 'Vodka Martini', 'Shake the vodka and vermouth together with a number of ice cubes, strain into a cocktail glass, add the olive and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qyxrqw1439906528.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2709, 'Vodka Russian', 'Mix it as a ordinary drink .', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rpttur1454515129.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2710, 'Waikiki Beachcomber', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ysuqus1441208583.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2711, 'Whiskey Sour', 'Shake with ice. Strain into chilled glass, garnish and serve. If served ''On the rocks'', strain ingredients into old-fashioned glass filled with ice.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/o56h041504352725.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2712, 'Whisky Mac', 'Pour both of the ingredients into a wine goblet with no ice.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yvvwys1461867858.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2713, 'White Lady', 'Add all ingredients into cocktail shaker filled with ice. Shake well and strain into large cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/jofsaz1504352991.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2714, 'White Russian', 'Pour vodka and coffee liqueur over ice cubes in an old-fashioned glass. Fill with light cream and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vsrupw1472405732.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2715, 'Whitecap Margarita', 'Place all ingredients in a blender and blend until smooth. This makes one drink.', 'Margarita/Coupette glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/srpxxp1441209622.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2716, 'Wine Cooler', 'Mix wine and soft drink. Pour into glass. Add ice.', 'Collins Glass', NULL, 'Optional alcohol', 'http://www.thecocktaildb.com/images/media/drink/yutxtv1473344210.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2717, 'Wine Punch', 'Combine all of the ingredients and pour over a block of ice.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/txustu1473344310.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2718, 'Yellow Bird', 'Shake and strain into a chilled cocktail glass', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/2t9r6w1504374811.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2719, 'Yoghurt Cooler', 'Place all ingredients in the blender jar - cover and whiz on medium speed until well blended. Pour in one tall, 2 medium or 3 small glasses and drink up. Note: Use lots of ice in this one - great on hot days! To add ice: Remove the center of the cover while the blender is on - drop 3 or 4 ice cubs and blend until they''re completely crushed.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/trttrv1441254466.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2720, 'Zambeer', 'Mix sambuca with rootbeer and stir. Add ice', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/bje5401485619578.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2721, 'Zenmeister', 'Mix together and enjoy', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qyuvsu1479209462.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2722, 'Ziemes Martini Apfelsaft', 'Serve without ice. At least the juice shold have room temperature.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xnzr2p1485619687.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2723, 'Zima Blaster', 'Fill glass with ice. Pour in Chambord, then fill with Zima. Mix and enjoy.', 'Hurricane glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/1wifuv1485619797.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2724, 'Zimadori Zinger', 'Pour Zima in a collins glass over ice and then pour the shot of Midori. Don''t stir. Garnish with a cherry.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/bw8gzx1485619920.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2725, 'Zinger', 'Get a shot glass and pour in three shots of the schnapps. Do the same with the Surge Cola. Then down it like Scheetz would.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/iixv4l1485620014.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2726, 'Zipperhead', 'Fill glass with rocks, add straw before putting in liquor. Then add the ingredients in order, trying to keep layered as much as possible (i.e. Chambord on bottom, then Vodka, Then soda on top).', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/r2qzhu1485620235.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2727, 'Zippy''s Revenge', 'Mix Kool-Aid to taste then add Rum and ammaretto. shake well to disolve the sugar in the Kool-Aid... serve cold', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/1sqm7n1485620312.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2728, 'Zizi Coin-coin', 'Pour 5cl of Cointreau on ice, add 2cl of fresh lemon (or lime) juice, stir gently, and finally add slices of lemon/lime in glass.', 'Margarita/Coupette glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/0fbo2t1485620752.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2729, 'Zoksel', 'No specific mixinginstructions, just poor every ingredient in one glass. The lemon goes with it.', 'Beer pilsner', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ft8ed01485620930.jpg', 'Soft Drink / Soda');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2730, 'Zorbatini', 'Prepare like a Martini. Garnish with a green olive.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wtkqgb1485621155.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2625, 'Rum Old-fashioned', 'Stir powdered sugar, water, and bitters in an old-fashioned glass. When sugar has dissolved add ice cubes and light rum. Add the twist of lime peel, float 151 proof rum on top, and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/otn2011504820649.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2626, 'Rum Punch', 'Mix all ingredients in a punch bowl and serve.', 'Punch bowl', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wyrsxu1441554538.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2627, 'Rum Runner', 'Mix all ingredients in glass & add ice.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vqws6t1504888857.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2628, 'Rum Screwdriver', 'Pour rum into a highball glass over ice cubes. Add orange juice, stir, and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/pwl7v81504886372.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2629, 'Rum Sour', 'In a shaker half-filled with ice cubes, combine the rum, lemon juice, and sugar. Shake well. Strain into a sour glass and garnish with the orange slice and the cherry.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/bylfi21504886323.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2630, 'Rum Toddy', 'Dissolve powdered sugar in water in an old-fashioned glass. Add rum and one ice cube and stir. Add the twist of lemon peel and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/athdk71504886286.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2631, 'Russian Spring Punch', 'Pour the ingredients into an highball glass, top with Sparkling wine.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ctt20s1504373488.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2632, 'Rusty Nail', 'Pour the Scotch and Drambuie into an old-fashioned glass almost filled with ice cubes. Stir well. Garnish with the lemon twist.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yqsvtw1478252982.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2633, 'Salty Dog', 'Pour all ingredients over ice cubes in a highball glass. Stir well and serve. (Vodka may be substituted for gin, if preferred.)', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/4vfge01504890216.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2634, 'San Francisco', 'Take a tall glass and put in a few ice cubes, fill the vodka over it and fill with juice then the \"creme\", to end fill in the grenadine but very carefully at the side of the glass so it will lay down in the bottom. garnish with orange and strawberry.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/szmj2d1504889961.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2635, 'Sangria #1', 'Mix all together in a pitcher and refrigerate. Add cloves and cinnamon sticks to taste. Serve in wine glasses.', 'Pitcher', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xrvxpp1441249280.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2636, 'Sangria - The World''s Best', 'Mix wine, sugar and fruit, and let sit in the fridge for 18-24 hours. The mixture will have a somewhat syrupy consistency. Before serving stir in brandy and cut the mixture with soda water until it have a thinner, more wine like consistency. Serve from a pitcher in wine glasses.', 'Pitcher', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vysywu1468924264.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2637, 'Sazerac', 'Rinse a chilled old-fashioned glass with the absinthe, add crushed ice, and set it aside. Stir the remaining ingredients over ice and set it aside. Discard the ice and any excess absinthe from the prepared glass, and strain the drink into the glass. Add the lemon peel for garnish.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vvpxwy1439907208.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2638, 'Scooter', 'Shake all ingredients well with cracked ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/twuptu1483388307.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2639, 'Scotch Cobbler', 'Pour scotch, brandy, and curacao over ice in an old-fashioned glass. Add the orange slice, top with the mint sprig, and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/1q7coh1504736227.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2640, 'Scotch Sour', 'Shake scotch, juice of lime, and powdered sugar with ice and strain into a whiskey sour glass. Decorate with 1/2 slice lemon, top with the cherry, and serve.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/0dnb6k1504890436.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2641, 'Scottish Highland Liqueur', 'Combine all ingreds in aging container. Cover tightly and shake gently several times during the first 24 hrs. After 24 hrs, remove the lemon zest. Cover again and let stand in a cool, dark place for 2 weeks, shaking gently every other day. Strain through a wire sieve to remove the angelica root and fennel. Return to aging container, cover and let stand undisturbed in a cool dark place for 6 months. Siphon or pour clear liqueur into a sterile bottle. The cloudy dregs may be saved for cooking.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/upqvvp1441253441.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2642, 'Screaming Orgasm', 'Pour first vodka, then Bailey''s, then Kahlua into a cocktail glass over crushed ice. Stir. Caution: use only high quality vodka. Cheap vodka can cause the Bailey''s to curdle. Test your brand of vodka by mixing 1 Tsp each of vodka and Bailey''s first.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/x894cs1504388670.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2643, 'Screwdriver', 'Mix in a highball glass with ice. Garnish and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/8xnyke1504352207.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2644, 'Sea breeze', 'Build all ingredients in a highball glass filled with ice. Garnish with lime wedge.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/7rfuks1504371562.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2645, 'Sex on the Beach', 'Build all ingredients in a highball glass filled with ice. Garnish with orange slice.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/twsvwr1469092365.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2646, 'Shanghai Cocktail', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ttyrxr1478820678.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2647, 'Shark Attack', 'Mix lemonade and water according to instructions on back of can. If the instructions say to add 4 1/3 cans of water do so. Mix into pitcher. Add 1 1/2 cup of Vodka (Absolut). Mix well. Pour into glass of crushed ice. Excellent!', 'Pitcher', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uv96zr1504793256.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2648, 'Sherry Eggnog', 'Shake sherry, powdered sugar, and egg with ice and strain into a collins glass. Fill with milk and stir. Sprinkle nutmeg on top and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xwrpsv1478820541.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2649, 'Sherry Flip', 'Shake all ingredients (except nutmeg) with ice and strain into a whiskey sour glass. Sprinkle nutmeg on top and serve.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qrryvq1478820428.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2650, 'Shot-gun', 'Pour one part Jack Daneils and one part Jim Beam into shot glass then float Wild Turkey on top.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/2j1m881503563583.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2651, 'Sidecar', 'Pour all ingredients into cocktail shaker filled with ice. Shake well and strain into cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/stwxuq1439906852.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2652, 'Sidecar Cocktail', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ewjxui1504820428.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2653, 'Singapore Sling', 'Pour all ingredients into cocktail shaker filled with ice cubes. Shake well. Strain into highball glass. Garnish with pineapple and cocktail cherry.', 'Hurricane glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tvtrrt1439906288.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2654, 'Sloe Gin Cocktail', 'Stir all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/d7mo481504889531.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2655, 'Smut', 'Throw it all together and serve real cold.', 'Beer mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rx8k8e1504365812.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2656, 'Snake Bite (UK)', 'Pour ingredients into a pint glass. Drink. Fall over.', 'Pint glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/xuwpyu1441248734.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2657, 'Snakebite and Black', 'Put blackcurrant squash in first up to about 1cm in glass. Then add the larger and cider one after another.', 'Pint glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rssvwv1441248863.jpg', 'Beer');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2658, 'Snowball', 'Place one ice cube in the glass and add 1 1/2 oz of Advocaat. Fill up the glass with lemonade and decorate with a slice of lemon. Serve at once.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/7ibfs61504735416.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2659, 'Sol Y Sombra', 'Shake ingredients with ice, strain into a brandy snifter, and serve. (The English translation of the name of this drink is \"Sun and Shade\", and after sampling this drink, you''ll understand why. Thanks, Kirby.)', 'Brandy snifter', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/3gz2vw1503425983.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2660, 'Space Odyssey', 'Fill glass with ice and add shots of Bacardi and Malibu. Add splash of pineapple juice and top with orange juice. Add grenadine for color and garnish with cherries.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vxtjbx1504817842.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2661, 'Spanish chocolate', 'Stir the milk with the chocolate and the cinnamon over low heat until the chocolate dissolves. Add the eggs and beat the mixture until it becomes thick, taking care not to boil. Serve in coffee mug.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/pra8vt1487603054.jpg', 'Cocoa');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2662, 'Spiced Peach Punch', 'Combine peach nectar, orange juice and brown sugar in a large saucepan. Tie cinnamon and cloves in a small cheesecloth bag. Drop into saucepan. Heat slowly, stirring constantly, until sugar dissolves. Simmer 10 minutes. Stir in lime juice. Serve in hot mugs.', 'Collins Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qxvypq1468924331.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2663, 'Spiking coffee', 'Incidentally, a pinch of cinnamon is indeed a nice addition to coffee but true heaven is a cardamom seed. Of course, you serve it in a coffee mug.', 'Coffee mug', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/isql6y1487602375.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2664, 'Spritz', 'Build into glass over ice, garnish and serve.', 'Old-Fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/j9evx11504373665.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2665, 'Stinger', 'Pour in a mixing glass with ice, stir and strain into a cocktail glass. May also be served on rocks in a rocks glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/2ahv791504352433.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2666, 'Stone Sour', 'Shake all ingredients with ice, strain into a chilled whiskey sour glass, and serve.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/vruvtp1472719895.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2667, 'Strawberry Daiquiri', 'Pour all ingredients into shaker with ice cubes. Shake well. Strain in chilled cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/deu59m1504736135.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2668, 'Strawberry Lemonade', 'Throw everything into a blender and mix until fairly smooth. Serve over ice.', 'Collins Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/spvvxp1468924425.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2669, 'Strawberry Margarita', 'Rub rim of cocktail glass with lemon juice and dip rim in salt. Shake schnapps, tequila, triple sec, lemon juice, and strawberries with ice, strain into the salt-rimmed glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tqyrpw1439905311.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2670, 'Strawberry Shivers', 'Place all ingredients in the blender jar - cover and whiz on medium speed until well blended. Pour in one tall, 2 medium or 3 small glasses and drink up.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/9h1vvt1487603404.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2671, 'Sunny Holiday Punch', 'Combine all ingredients in a punch bowl.', 'Punch bowl', NULL, 'Optional alcohol', 'http://www.thecocktaildb.com/images/media/drink/rywtwy1468924758.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2672, 'Surf City Lifesaver', 'Lots of ice and soda top up in tall glass with cherry and a grin.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/2rzfer1487602699.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2673, 'Swedish Coffee', 'Pour the coffee in an ordinary coffee cup. Add the aquavit. Add sugar by taste. Stir and have a nice evening (morning)', 'Coffee Mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ywtrvt1441246783.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2674, 'Sweet Bananas', 'Place all ingredients in the blender jar - cover and whiz on medium speed until well blended. Pour in one tall, 2 medium or 3 small glasses and drink up.', 'Highball Glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sxpcj71487603345.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2675, 'Sweet Sangria', 'Dissolve the sugar in hot water and cool. Peel the citrus fruits and break into wedges. Mix the wine, sugar syrup, fruit, and Fresca in a pitcher and put in the fridge for a few hours. Serve in tall glasses with a straw.', 'Pitcher', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/uqqvsp1468924228.jpg', 'Punch / Party Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2676, 'Sweet Tooth', 'Put 2 shots Godiva Liquour into a glass, add as much or as little milk as you would like.', 'Highball Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/j6rq6h1503563821.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2677, 'Talos Coffee', 'Add your GM and then add your coffee.', 'Brandy snifter', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rswqpy1441246518.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2678, 'Tennesee Mud', 'Mix Coffee, Jack Daniels and Amaretto. Add Cream on top.', 'Coffee Mug', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/txruqv1441245770.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2679, 'Tequila Fizz', 'Shake all ingredients (except ginger ale) with ice and strain into a collins glass over ice cubes. Fill with ginger ale, stir, and serve.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/2bcase1504889637.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2680, 'Tequila Sour', 'Shake tequila, juice of lemon, and powdered sugar with ice and strain into a whiskey sour glass. Add the half-slice of lemon, top with the cherry, and serve.', 'Whiskey sour glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ek0mlq1504820601.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2681, 'Tequila Sunrise', 'Pour the tequila and orange juice into glass over ice. Add the grenadine, which will sink to the bottom. Stir gently to create the sunrise effect. Garnish and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/quqyqp1480879103.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2682, 'Tequila Surprise', 'Fill shot glass with Tequila. Add drops of Tobasco sauce.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/8189p51504735581.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2683, 'Texas Rattlesnake', 'Mix all ingredients and Shake well. Sweet at first, with a BITE at the end...', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rtohqp1504889750.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2684, 'Texas Sling', 'Blend with Ice until smooth. Serve in a tulip glass, top with whip cream.', 'Wine Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ypl13s1504890158.jpg', 'Milk / Float / Shake');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2685, 'Thai Coffee', 'Place the coffee and spices in the filter cone of your coffee maker. Brew coffee as usual, let it cool. In a tall glass, dissolve 1 or 2 teaspoons of sugar in an ounce of the coffee (it''s easier to dissolve than if you put it right over ice). Add 5-6 ice cubes and pour coffee to within about 1 inch of the top of the glass. Rest a spoon on top of the coffee and slowly pour whipping cream into the spoon. This will make the cream float on top of the coffee rather than dispersing into it right away.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/wquwxs1441247025.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2686, 'Thai Iced Coffee', 'Prepare a pot of coffee at a good European strength. In the ground coffee, add 2 or 3 freshly ground cardamom pods. Sweeten while hot, then cool quickly. Serve in highball glass over ice, with cream. To get the layered effect, place a spoon atop the coffee and pour the milk carefully into the spoon so that it floats on the top of the coffee.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rqpypv1441245650.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2687, 'Thai Iced Tea', 'Combine Thai tea (i.e., the powder), boiling water, and sweetened condensed milk, stir until blended. Pour into 2 tall glasses filled with ice cubes. Garnish with mint leaves. Makes 2 servings.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/trvwpu1441245568.jpg', 'Coffee / Tea');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2688, 'The Evil Blue Thing', 'Pour ingredients into glass, and drop in a blue whale! The blue whale isn''t really necessary, but it makes the drink more \"fun\".', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/ojnpz71504793059.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2689, 'Thriller', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a cocktail glass.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rvuswq1461867714.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2690, 'Tia-Maria', 'Boil water, sugar and coffe for 10 mins and let cool. Add rum and vanilla. Put in clean bottle(s) and leave for 1 week before using.', 'Collins Glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/sih81u1504367097.jpg', 'Homemade Liqueur');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2691, 'Tom Collins', 'In a shaker half-filled with ice cubes, combine the gin, lemon juice, and sugar. Shake well. Strain into a collins glass alomst filled with ice cubes. Add the club soda. Stir and garnish with the cherry and the orange slice.', 'Collins glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/qystvv1439907682.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2692, 'Tomato Tang', 'Place all ingredients in the blender jar - cover and whiz on medium speed until well blended. Pour in one tall, 2 medium or 3 small glasses and drink up.', 'Highball glass', NULL, 'Non alcoholic', 'http://www.thecocktaildb.com/images/media/drink/869qr81487603278.jpg', 'Other/Unknown');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2693, 'Tommy''s Margarita', 'Shake and strain into a chilled cocktail glass.', 'Old-Fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/loezxn1504373874.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2694, 'Turf Cocktail', 'Stir all ingredients (except orange peel) with ice and strain into a cocktail glass. Add the twist of orange peel and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/utypqq1441554367.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2695, 'Turkeyball', 'Shake with ice and strain into a shot glass.', 'Shot glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/rxurpr1441554292.jpg', 'Shot');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2696, 'Tuxedo Cocktail', 'Stir all ingredients with ice and strain into a cocktail glass. Garnish with a cherry and a twist of lemon zest.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/4u0nbl1504352551.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2697, 'Valencia Cocktail', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/9myuc11492975640.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2698, 'Vampiro', 'Vampiros may be made in a tall glass or an old fashioned glass. Bartenders may first \"rim\" the glass with Kosher Salt, which is done by placing a layer of Kosher Salt on a chopping board, moistening the glass'' rim with lime juice or water, and then placing the upside down glass rim onto the Kosher Salt, so that the salt sticks to the moistened rim. The second step is to fill half the glass with ice and add one or two shooter glasses full of high quality Tequila. The next stage is to add the flavouring elements. This is done by squeezing a fresh lime into the glass, adding a few grains of salt, adding citrus-flavoured soda pop, until the glass is 4/5 full, and then adding spicy Viuda de Sanchez (or orange juice, lime juice and pico de gallo). The final step is to stir the ingredients so that the flavours are properly blended.', 'Old-Fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/yfhn371504374246.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2699, 'Van Vleet', 'Shake all ingredients with ice, strain into an old-fashioned glass over ice cubes, and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/fgq2bl1492975771.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2700, 'Vermouth Cassis', 'Stir vermouth and creme de cassis in a highball glass with ice cubes. Fill with carbonated water, stir again, and serve.', 'Highball glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/tswpxx1441554674.jpg', 'Ordinary Drink');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2701, 'Vesper', 'Shake over ice until well chilled, then strain into a deep goblet and garnish with a thin slice of lemon peel.', 'Cocktail glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/mtdxpa1504374514.jpg', 'Cocktail');");
            sqLiteDatabase.execSQL("INSERT INTO cocktail VALUES (2702, 'Vesuvio', 'Shake all ingredients with ice, strain into an old-fashioned glass over ice cubes, and serve.', 'Old-fashioned glass', NULL, 'Alcoholic', 'http://www.thecocktaildb.com/images/media/drink/26cq601492976203.jpg', 'Ordinary Drink');");
